import React, { useState } from "react";
import { Switch } from "react-native-paper";
import { View, Text } from "react-native";

export default function ThemeToggler() {
  const [isDarkOn, toggleTheme] = useState(true);
  return (
    <View
      style={{
        flexDirection: "row",
        paddingRight: 5,
        backgroundColor: "rgba(0, 0, 0, 0)"
      }}
    >
      <Switch
        value={isDarkOn}
        onValueChange={() => toggleTheme((cur) => !cur)}
      />
      <Text>{isDarkOn ? "üåô" : "‚òÄÔ∏è"}</Text>
    </View>
  );
}
