import React from "react";
import { ScrollView, Text, StyleSheet, FlatList } from "react-native";
import { DataTable } from "react-native-paper";
import {
  Table,
  TableWrapper,
  Row,
  Rows,
  Col,
  Cols,
  Cell
} from "react-native-table-component";

const PLAYERS = ["Vivian", "Sarita", "Flávio", "Debora"];

const SCORE_TABLE = {
  tableHead: ["", "Head1", "Head2", "Head3"],
  tableTitle: ["Title", "Title2", "Title3", "Title4"],
  tableData: [
    ["1", "2", "3"],
    ["a", "b", "c"],
    ["1", "2", "3"],
    ["a", "b", "c"]
  ]
};

const TableTitle = ({ player }) => {
  return (
    <DataTable.Cell style={styles.title}>
      <Text style={styles.titleText}>{player}</Text>
    </DataTable.Cell>
  );
};

const TableCell = ({ score }) => {
  return (
    <DataTable.Cell style={styles.title}>
      <Text style={styles.cellText}>{score}</Text>
    </DataTable.Cell>
  );
};

const ScoreBoardScreen = () => {
  return (
    <ScrollView>
      <Table borderStyle={{ borderWidth: 1 }}>
        <Row
          data={state.tableHead}
          flexArr={[1, 2, 1, 1]}
          style={styles.head}
          textStyle={styles.text}
        />
        <TableWrapper style={styles.wrapper}>
          <Col
            data={state.tableTitle}
            style={styles.title}
            heightArr={[28, 28]}
            textStyle={styles.text}
          />
          <Rows
            data={state.tableData}
            flexArr={[2, 1, 1]}
            style={styles.row}
            textStyle={styles.text}
          />
        </TableWrapper>
      </Table>
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  dataTable: {
    backgroundColor: "pink",
    flex: 1
  },
  title: {
    flex: 1,
    marginHorizontal: 30
  },
  titleText: {
    textAlign: "center",
    fontSize: 16,
    fontWeight: "bold",
    color: "black",
    flex: 1
  },
  cellText: {
    textAlign: "center",
    fontSize: 14,
    fontWeight: "bold",
    color: "white",
    flex: 1
  }
});

export default ScoreBoardScreen;
