import React from "react";
import { ScrollView, Text, StyleSheet } from "react-native";
import { DataTable } from "react-native-paper";
import {
  Table,
  TableWrapper,
  Row,
  Rows,
  Col
} from "react-native-table-component";

const SCORE_TABLE = {
  tableHead: ["", "Head1", "Head2", "Head3"],
  tableTitle: ["Title", "Title2", "Title3", "Title4"],
  tableData: [
    ["1", "2", "3"],
    ["a", "b", "c"],
    ["1", "2", "3"],
    ["a", "b", "c"]
  ]
};

/* const TableTitle = ({ player }) => {
  return (
    <DataTable.Cell style={styles.title}>
      <Text style={styles.titleText}>{player}</Text>
    </DataTable.Cell>
  );
};

const TableCell = ({ score }) => {
  return (
    <DataTable.Cell style={styles.title}>
      <Text style={styles.cellText}>{score}</Text>
    </DataTable.Cell>
  );
}; */

const ScoreBoardScreen = () => {
  return (
    <ScrollView>
      <Table borderStyle={{ borderWidth: 1 }}>
        <Row
          data={SCORE_TABLE.tableHead}
          flexArr={[1, 2, 2, 2]}
          style={styles.head}
          textStyle={styles.text}
        />
        <TableWrapper style={styles.wrapper}>
          <Col
            data={SCORE_TABLE.tableTitle}
            style={styles.title}
            heightArr={[28, 28]}
            textStyle={styles.text}
          />
          <Rows
            data={SCORE_TABLE.tableData}
            flexArr={[2, 2, 2]}
            style={styles.row}
            textStyle={styles.text}
          />
        </TableWrapper>
      </Table>
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: { flex: 1, padding: 16, paddingTop: 30, backgroundColor: "#fff" },
  head: { height: 40, backgroundColor: "#f1f8ff" },
  wrapper: { flexDirection: "row" },
  title: { flex: 1, backgroundColor: "#f6f8fa" },
  row: { height: 28 },
  text: { textAlign: "center", color: "peach" }
});

export default ScoreBoardScreen;
